let backendHost;

const hostname = window && window.location && window.location.hostname;

if (hostname === "localhost"){
    backendHost = "http://localhost:8080";
} else{
    backendHost = "http://182.230.225.99:9988";
}

export const API_BASE_URL = `${backendHost}`;
